// Access data from the context
const seriesData = context.panel.data?.series;

if (!seriesData || seriesData.length === 0) {
  return { title: { text: 'No data', left: 'center' } };
}

const firstSeries = seriesData[0];
const fields = firstSeries.fields;

if (!fields || fields.length < 2) {
  return { title: { text: 'Invalid data structure', left: 'center' } };
}

const actionSetField = fields.find(f => f.name === 'action_set_id');
const avgKpiField = fields.find(f => f.name === 'avg_kpi');

if (!actionSetField || !avgKpiField) {
  return { title: { text: 'Missing required fields', left: 'center' } };
}

const categories = actionSetField.values.toArray();
const values = avgKpiField.values.toArray();

if (categories.length === 0) {
  return { title: { text: 'No data available', left: 'center' } };
}

const maxValue = Math.max.apply(null, values);
const scaleMax = Math.ceil(maxValue * 1.2 / 100) * 100;

return {
  title: {
    text: 'KPI Performance Radar - horizon_tv',
    left: 'center',
    textStyle: { fontSize: 16 }
  },
  tooltip: {
    trigger: 'item'
  },
  legend: {
    data: ['Average KPI (ms)'],
    bottom: 10
  },
  radar: {
    indicator: categories.map(function(cat, idx) {
      return { 
        name: cat, 
        max: scaleMax 
      };
